import { useEffect, useState } from 'react';
import BlogView from '../components/BlogView';
import { useParams } from 'react-router-dom'; // Import useParams to get URL params

const Blog = () => {
    const { id } = useParams(); // Extract blog ID from URL parameters
    const [blog, setBlog] = useState(null);

    useEffect(() => {
        const fetchBlog = async () => {
            const response = await fetch(`/api/blogs/${id}`); // Fetch blog by ID
            const json = await response.json();

            if (response.ok) {
                setBlog(json);
            }
        };
        fetchBlog();
    }, [id]); // Depend on id so it fetches when ID changes

    if (!blog) {
        return <p>Loading...</p>; // Handle loading state
    }

    return (
        <BlogView blog={blog} />
    );
};

export default Blog;
