import React from 'react';
import blogPic from '../pages/img/blog.jpg';
import { useNavigate } from 'react-router-dom';

const BlogOutView = ({ blog }) => {
    const navigate = useNavigate();

    const routeBlog = () => {
        navigate('/blog');
    };

    // Check if blog is defined and set default values if necessary
    const title = blog ? blog.title : 'No Title';
    const author = blog ? blog.author : 'Unknown Author';
    const date = blog ? new Date(blog.createdAt).toLocaleDateString() : 'Unknown Date';
    const upvotes = blog ? blog.upvotes : 0;
    const downvotes = blog ? blog.downvotes : 0;

    return (
        <div className="blog-div" onClick={routeBlog}>
            <img src={blogPic} alt="Blog" className="blog-image" />
            <h3 className="blog-title">{title}</h3>
            <p className="blog-author">Author: {author}</p>
            <p className="blog-date">Date: {date}</p>
            <div className="blog-votes">
                <span className="upvote-button" onClick={(e) => e.stopPropagation()}>▲ {upvotes}</span>
                <span className="downvote-button" onClick={(e) => e.stopPropagation()}>▼ {downvotes}</span>
            </div>
        </div>
    );
};

export default BlogOutView;
