import React, { useEffect } from 'react';
import './Login.css'; 

const Login = () => {

    useEffect(() => {
        const imgBtn = document.querySelector('.img-btn');
        const cont = document.querySelector('.cont');

        const toggleClass = () => {
            cont.classList.toggle('s-signup');
        };

        imgBtn.addEventListener('click', toggleClass);

        // Cleanup function to remove the event listener
        return () => {
            imgBtn.removeEventListener('click', toggleClass);
        };
    }, []);

    return (
        <div>
            <h1>Log in</h1>
            <div className="cont">
                <div className="form sign-in">
                    <h2>Sign In</h2>
                    <label>
                        <span>Email Address</span>
                        <input type="email" name="email"/>
                    </label>
                    <label>
                        <span>Password</span>
                        <input type="password" name="password"/>
                    </label>
                    <button className="submit" type="button">Sign In</button>
                </div>

                <div className="sub-cont">
                    <div className="img">
                        <div className="img-text m-up">
                            <h1>New here?</h1>
                            <p>Sign up and discover</p>
                        </div>
                        <div className="img-text m-in">
                            <h1>One of us?</h1>
                            <p>Just sign in</p>
                        </div>
                        <div className="img-btn">
                            <span className="m-up">Sign Up</span>
                            <span className="m-in">Sign In</span>
                        </div>
                    </div>
                    <div className="form sign-up">
                        <h2>Sign Up</h2>
                        <label>
                            <span>Name</span>
                            <input type="text"/>
                        </label>
                        <label>
                            <span>Email</span>
                            <input type="email"/>
                        </label>
                        <label>
                            <span>Password</span>
                            <input type="password"/>
                        </label>
                        <label>
                            <span>Confirm Password</span>
                            <input type="password"/>
                        </label>
                        <button type="button" className="submit">Sign Up Now</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
