import blogPic from '../pages/img/blog.jpg';
import icon from '../pages/img/icon.jpg';
import { useParams } from 'react-router-dom'; // Import useParams
import { useState, useEffect } from 'react';

// Example blog data
const blogData = [
    { id: '1', title: 'Sample Blog Title 1', author: 'Author 1', date: 'Date 1', description: 'Description 1', content: 'Content 1' },
    { id: '2', title: 'Sample Blog Title 2', author: 'Author 2', date: 'Date 2', description: 'Description 2', content: 'Content 2' }
    // Add more blog objects as needed
];

const Blog = () => {
    const { id } = useParams(); // Get the blog ID from the URL
    const [blog, setBlog] = useState(null);

    useEffect(() => {
        // Fetch or filter the blog data based on the ID
        const fetchedBlog = blogData.find(blog => blog.id === id);
        setBlog(fetchedBlog);
    }, [id]);

    if (!blog) {
        return <div>Loading...</div>; // Handle loading state or if the blog is not found
    }

    return (
        <div className="blog-container">
            <div className='blog-header'>
                <img src={blogPic} alt='Blog Header'/>
                <h1>{blog.title}</h1>
                <h3>Published by: {blog.author} <span className="secondary-text">{blog.date}</span></h3> 
                <h4>{blog.description}</h4>
            </div>
            <div className='blog-info'>
                <p>{blog.content}</p>
            </div>
            <div className='comment-section'>
                <div className='comment-header'>
                    <h3>Comment Section</h3>
                    <div className="vote-section">
                        <div className="vote-button upvote"></div>
                        <div className="vote-count">0</div>
                        <div className="vote-button downvote"></div>
                    </div>
                </div>
                <div className="comment-input-container">
                    <img src={icon} alt="icon"/>
                    <input type='text' className='comment-here' placeholder='Add a comment...' />
                    <button className='post-comment'>Post</button>
                </div>
                <p className='other-comments'>
                    <img src={icon} alt='icon'/>
                    <span>DISPLAY COMMENTS HERE</span>
                </p>
            </div>
        </div>
    )
}

export default Blog;
