import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logPic from './img/log-pic.png';
import { useLogin } from '../hooks/useLogin';

const Login = () => {
    const [errors, setErrors] = useState([]);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const { login, error } = useLogin();

    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();

        let errors = [];

        if (email.length === 0 && password.length === 0) {
            errors.push('All fields are required.');
        } else {
            if (email.length === 0) {
                errors.push('Please enter your email.');
            }
            if (password.length === 0) {
                errors.push('Please enter your password.');
            }
        }

        if (errors.length > 0) {
            setErrors(errors);
            return; // Exit if there are validation errors
        }

        setErrors([]);
        setIsLoading(true);

        try {
            const response = await login(email, password);

            if (response.ok) {
                console.log('Login successful', response);
                navigate('/dashboard'); // Redirect to the desired route
            } else {
                setErrors([response.message || 'Login failed']);
            }
        } catch (err) {
            setErrors([err.message || 'An error occurred']);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-all">
                <div className="log-info">
                    <h1>Welcome back!</h1>
                    <form className='login' onSubmit={handleLogin}>
                        <label htmlFor="email">Email</label>
                        <input
                            className="email"
                            type="text"
                            id="email"
                            placeholder='Enter your email here...'
                            onChange={(e) => setEmail(e.target.value)}
                            value={email}
                        />
                        <label htmlFor="password">Password</label>
                        <input
                            className="password"
                            type="password"
                            id="password"
                            placeholder='Enter your password here...'
                            onChange={(e) => setPassword(e.target.value)}
                            value={password}
                        />
                        <button type="submit" disabled={isLoading}>Login</button>
                    </form>
                    {errors.length > 0 && (
                        <div className="error-messages">
                            {errors.map((error, index) => (
                                <p key={index} className="error">{error}</p>
                            ))}
                        </div>
                    )}
                    <p>Don't have an account? Sign up <a href="/register">here</a>.</p>
                </div>
                <div className="log-img">
                    <img src={logPic} alt="Login illustration" />
                </div>
            </div>
        </div>
    );
};

export default Login;
