import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import defaultIcon from './img/default-icon.jpg'; // Default profile picture

const EditProfile = () => {
  const [profilePic, setProfilePic] = useState(defaultIcon);
  const [email, setEmail] = useState('EMAIL HERE');
  const [password, setPassword] = useState('PASSWORD IN *');

  const handleProfilePicChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setProfilePic(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
  };

  return (
    <div className="edit-profile-container">
      <div className="edit-profile-settings">
        <div className="redirection">
          <p><Link to='/home'>Home</Link> / <Link to='/profile'>Profile</Link> / Edit</p>
        </div>
        <div className='edit-profile-main-details'>
          <img src={profilePic} alt="profile-picture" className="profile-picture"/>
          <form onSubmit={handleSubmit} className="edit-form">
            <div className="form-group">
              <label htmlFor="profile-pic">Change Profile Picture</label>
              <input
                type="file"
                id="profile-pic"
                accept="image/*"
                onChange={handleProfilePicChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="form-buttons">
              <button type="submit" className="btn save-btn">Save Changes</button>
              <Link to='/profile' className="btn cancel-btn">Cancel</Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default EditProfile;
