import { useState } from 'react';
import logPic from './img/log-pic.png';
import { useLogin } from "../hooks/useLogin"

const Login = () => {

    const [errors, setErrors] = useState([]);
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const {login, error, isLoading} = useLogin()

    const handleLogin = async (e) => {

        let errors = [];

        let email = document.getElementById('email').value;
        let pass = document.getElementById('password').value;

        if (email.length===0 && pass.length===0){
            errors.push('All fields are required.')
        }
        else {
            if (email.length===0){
                errors.push('Please enter your email.')
            }
            if (pass.length===0){
                errors.push('Please enter your password.')
            }
        }

        setErrors(errors)

        e.preventDefault()
        await login(email, password)


    }

    return (
        <div className="login-container">
            <div className="login-all">
                <div className="log-info">
                    <h1>Welcome back!</h1>
                    <label htmlFor="email">Email</label>
                    <input className="email" type="text" id="email" placeholder='Enter your email here...' onChange={(e) => setEmail(e.target.value)} />
                    <label htmlFor="password">Password</label>
                    <input className="password" type="password" id="password" placeholder='Enter your password here...' onChange={(e) => setPassword(e.target.value)}  />
                    <button onClick={handleLogin}>Login</button>

                    {errors.length > 0 && (
                        <div className="error-messages">
                            {errors.map((error, index) => (
                                <p key={index} className="error">{error}</p>
                            ))}
                        </div>
                    )}

                    <p>Don't have an account? Sign up <a href="/register">here</a>.</p>
                </div>

                <div className="log-img">
                    <img src={logPic} alt="Login illustration" /> 
                </div>
            </div>
        </div>
    );
}

export default Login;
